refsection.programlisting:
t = linspace(0,1,1000);
x = 4*sin(4*%pi*t);
x = x - sign(t - .3) - sign(.72 - t);
W = ndwt(x,4,'db2','mode','per');
d1 = indwt(W,'d',1);
scf();
subplot(211);
plot(t,x); title('Original Signal');
subplot(212);
plot(t,d1,'linewidth',2); title('Wavelet Approximation -- Level 1');
